function createButtons(){for(i=2;i<=11;i++){var a='<button class="group-number" data-number='+[i]+">"+i+"</button>";$(".group-amount").append(a)}}function createGroups(){for(var a=2;groupTotal>=a;a++)$(".display-groups").append('<div class="teams'+a+'">Team: '+[a]+"</div>")}function displayStudents(a){var b=2;for(i=2;i<a.length;i++)$(".teams"+b).append("<p>"+studentArray[i]+"</p>"),groupTotal>b?b++:b=2}function shuffleStudents(a){for(var b,c,d=a.length;0!==d;)c=Math.floor(Math.random()*d),d-=1,b=a[d],a[d]=a[c],a[c]=b;return a}function getData(){$.ajax({url:"/data",success:function(a){$.each(a,function(){studentArray.push(this.name)}),createButtons()}})}var studentArray=[],groupTotal=0;$(document).ready(function(){getData(),$(".group-amount").on("click",".group-number",function(){groupTotal=$(this).data("number"),console.log(groupTotal)}),$("#randomizer").on("click",function(){shuffleStudents(studentArray),createGroups(studentArray),displayStudents(studentArray),console.log("shuffling: "+studentArray)})});